mapper.update:
UPDATE class 
set C_TITLE = #{cTitle},C_IMG = #{cImg},C_DESC= #{cDesc},VEGTYPE= #{vegType},C_PRICE = #{cPrice},
C_START = #{cStart},C_END = #{cEnd},STATUS = #{status},CURRICULUM = #{curriCulum},C_PARTI = #{cParti},
C_LOC = #{cLoc},C_TIME = #{cTime}
where c_id = #{cId}

UPDATE member
set career = #{career}
WHERE
m_id=#{mId}

UPDATE enquiry
set enq_Result = #{enqResult},
enq_pro_date = current_date,
enq_type = #{enqType}
WHERE
enq_no=#{enqNo}

