mapper.select:
SELECT a.*
FROM ( SELECT b.*, ROWNUM rn
FROM
(SELECT *
FROM class


		and vegType in('비건','락토','오보','락토오보')


		and vegType in('비건','락토','오보','락토오보')
		and c_title like '%' || #{keyword} || '%'


		and vegType = #{vegType}


		and vegType = #{vegType}
		and c_title like '%' || #{keyword} || '%'


) b
) a
WHERE rn BETWEEN #{start } AND #{end }

mapper.update:
UPDATE class
set c_hit = c_hit + 1
WHERE c_id = #{cId}
