mapper.select:
SELECT a.*
FROM ( SELECT b.*, ROWNUM rn
		FROM
			(SELECT *
			FROM class


					AND vegType = #{vegType}


					AND UPPER(c_title) LIKE UPPER('%' || #{keyword} || '%')


		) b
	) a
WHERE rn BETWEEN #{start } AND #{end }

mapper.update:
UPDATE class
SET c_hit = c_hit + 1
WHERE c_id = #{cId}

mapper.select:
SELECT count(*)
FROM like_list
WHERE m_id = #{mId}
AND origin_id = #{originId}

mapper.delete:
DELETE FROM like_list
WHERE origin_id = #{originId}
AND m_id = #{mId}

mapper.update:
UPDATE class
SET c_join = c_join + 1
WHERE c_id = #{cId}

UPDATE member
SET wallet_point = #{walletPoint},
wallet_cash = #{walletCash}
WHERE m_id = #{mId}
