mapper.update:
UPDATE member
SET password = #{ password,	jdbcType=VARCHAR},
vegtype = #{ vegtype, jdbcType=VARCHAR},
profile_image = #{profileImage, jdbcType=VARCHAR}
WHERE m_id=#{ mId}

mapper.select:
SELECT count(*) 
FROM REST_RESERV
WHERE m_id = #{mId }

SELECT count(*)
FROM Recipe
WHERE m_id = #{mId }

mapper.delete:
DELETE from like_list
WHERE origin_id = #{originId}
AND m_id = #{mId}
