mapper.select:
SELECT a.*
FROM ( SELECT b.*, ROWNUM rn
FROM
(SELECT *
FROM class


		and vegType in('비건','락토','오보','락토오보')


		and vegType in('비건','락토','오보','락토오보')
		and c_title like '%' ||
		#{keyword} || '%'


		and vegType = #{vegType}


		and vegType = #{vegType}
		and c_title like '%' || #{keyword} ||
		'%'


) b
) a
WHERE rn BETWEEN #{start } AND #{end }

mapper.delete:
DELETE FROM like_list
WHERE origin_id=#{originId}
AND
m_id=#{mId}

mapper.select:
SELECT COUNT(*)
FROM
like_list
WHERE m_id=#{mId}
AND origin_id=#{originId}
