mapper.select:
SELECT a.* FROM ( SELECT b.*, ROWNUM rn FROM (SELECT distinct(r.r_id), r.r_type, r.r_title, r.r_date, r.r_content, r.r_image, r.r_hit, r.m_id, r.c_id, r.r_report FROM recipe r join recipe_material rm on r.r_id = rm.r_id
r.r_type = #{RType}
r.r_title like '%' || #{keyword} || '%' OR rm.mat_name like '%' || #{keyword} || '%'
) b ) a WHERE rn BETWEEN #{start } AND #{end }

SELECT a.*
FROM ( SELECT b.*, ROWNUM rn
FROM
(SELECT *
FROM class


		and vegType in('비건','락토','오보','락토오보')


		and vegType in('비건','락토','오보','락토오보')
		and c_title like '%' ||
		#{keyword} || '%'


		and vegType = #{vegType}


		and vegType = #{vegType}
		and c_title like '%' || #{keyword} ||
		'%'


) b
) a
WHERE rn BETWEEN #{start } AND #{end }

mapper.delete:
DELETE FROM like_list
WHERE origin_id=#{originId}
AND
m_id=#{mId}

mapper.select:
SELECT COUNT(*)
FROM
like_list
WHERE m_id=#{mId}
AND origin_id=#{originId}
