mapper.select:
SELECT a.* FROM ( SELECT b.*, ROWNUM rn FROM (SELECT distinct(r.r_id), r.r_type, r.r_title, r.r_date, r.r_content, r.r_image, r.r_hit, r.m_id, r.c_id, r.r_report FROM recipe r LEFT OUTER JOIN recipe_material rm on r.r_id = rm.r_id
r.r_type = #{RType}
UPPER(r.r_title) like UPPER('%' || #{keyword} || '%') OR UPPER(rm.mat_name) like UPPER('%' || #{keyword} || '%')
) b ) a WHERE rn BETWEEN #{start } AND #{end }

select count(distinct(r.r_id)) from recipe r left outer JOIN recipe_material rm on r.r_id = rm.r_id
r.r_type = #{RType}
UPPER(r.r_title) like UPPER('%' || #{keyword} || '%') OR UPPER(rm.mat_name) like UPPER('%' || #{keyword} || '%')

SELECT a.*
FROM ( SELECT b.*, ROWNUM rn
FROM
(SELECT *
FROM class


		and vegType in('비건','락토','오보','락토오보')


		and vegType in('비건','락토','오보','락토오보')
		and c_title like '%' ||
		#{keyword} || '%'


		and vegType = #{vegType}


		and vegType = #{vegType}
		and c_title like '%' || #{keyword} ||
		'%'


) b
) a
WHERE rn BETWEEN #{start } AND #{end }

SELECT c.c_id "cId", c.c_title "cTitle", c.c_desc "cDesc", m.m_name "lName" FROM class c join member m ON c.m_id = m.m_id
and c.vegType = #{vegType}
and c.c_title like '%' || #{keyword} || '%'

mapper.delete:
DELETE FROM like_list
WHERE origin_id=#{originId}
AND
m_id=#{mId}

mapper.select:
SELECT COUNT(*)
FROM
like_list
WHERE m_id=#{mId}
AND origin_id=#{originId}

mapper.update:
UPDATE member
SET wallet_point = wallet_point + 50
WHERE m_id = #{mId}
