mapper.select:
SELECT * FROM restaurant
WHERE m_id=#{mId}
order by rest_id

SELECT * FROM rest_reserv
WHERE rest_id = #{restId}
AND rest_reserv_status = '대기'

mapper.update:
UPDATE rest_menu
SET menu_name = #{menuName},
menu_vege_type = #{menuVegeType},
menu_price = #{menuPrice}
WHERE
menu_id=#{menuId}

UPDATE rest_reserv
SET rest_reserv_status = #{restReservStatus}
WHERE rest_reserv_num = #{restReservNum}

UPDATE restaurant SET rest_reserv_wait = rest_reserv_wait - 1
, rest_reserv_accept = rest_reserv_accept + 1
WHERE rest_id = #{restId}
