mapper.select:
SELECT a.* FROM ( SELECT b.*, ROWNUM rn FROM (SELECT * FROM meet_un
current_date < meet_start
current_date BETWEEN meet_start AND meet_end
current_date > meet_end
AND meet_title like '%' || #{keyword} || '%'
) b ) a WHERE rn BETWEEN #{start } AND #{end }

SELECT ceil(((select meet_start
from meet_un where meet_id = #{meetId}) - current_date))
FROM DUAL

mapper.delete:
DELETE FROM like_list
WHERE origin_id=#{originId}
AND
m_id=#{mId}

mapper.select:
SELECT COUNT(*)
FROM
like_list
WHERE m_id=#{mId}
AND origin_id=#{originId}

mapper.update:
UPDATE meet_un
SET meet_participant = meet_participant
+ 1,
meet_fund = meet_fund + #{myFund}
WHERE meet_id = #{meetId}

UPDATE member
SET wallet_point = #{walletPoint},
wallet_cash = #{walletCash}
WHERE m_id = #{mId}

mapper.select:
SELECT *
FROM meet_parti
WHERE m_id=#{mId} AND meet_id=#{meetId}

mapper.update:
UPDATE member
SET wallet_cash = wallet_cash + #{payBack}
WHERE m_id=#{mId}

UPDATE member
SET wallet_point = wallet_point + #{reward}
WHERE m_id=#{mId}
